private void encryptCardData (TLCapture tl, Card card)      
   throws Exception {
     Map<String,String> m = new HashMap<>();
     m.put ("P", card.getPan());
     m.put ("E", card.getExp());
     SecureData sd = getCryptoService().aesEncrypt(         
        Serializer.serializeStringMap(m)
     );
     tl.setKid(sd.getId());                                 
     tl.setSecureData(sd.getEncoded());                     
 }

